//breakpoint
$breakpoint-mobile:450px;
$breakpoint-tablet:758px;
$breakpoint-desktop:1024px;

//mediaquery mixin
@mixin mobile{
    @media (max-width:#{$breakpoint-tablet - 1px}) {
        @content;
    }
}

@mixin tablet{
    @media (min-width:#{$breakpoint-tablet}) and (max-width:#{$breakpoint-desktop - 1px}){
        @content;
    }
}

@mixin desktop{
    @media (min-width:#{$breakpoint-desktop}){
        @content;
    }
}


body,html{
    margin:0;
    padding:0;
}
.content-first{
    height:100vh;
    position: relative;
    .content-header{
        background-color: none;
        display: flex;
        justify-content: space-evenly;
        align-items:flex-end;

        span{
            &:nth-child(2){
                color:#fff;
            }
            &:hover, &:active{
                transform: scale(1.2);
            }
            font-size:25px;
            font-weight: 700;
            cursor:pointer;
            transition: all .15s;
        }
        .img-div{

                img{
                    width:130px;
                    height:130px;
                }
        }
    }
    .content-main{
        height:100vh;
        position:absolute;
        top:0;
        left:0;
        right:0;
        bottom:0;
        z-index:-1;
        .main-img{
            height:100vh;
            width:100vw;
        }
        .main-video{
            position: absolute;
            top: 35%;
            right: 14%;
            width: 38vw;
            height: 439px;
        }
        .greeting-main{
            z-index: 1;
            position:absolute;
            top:40%;
            left:10%;
            letter-spacing: -1px;
            font-size:1.3em;
            .greeting1{
                font-weight: 700;
                font-size:50px;
                margin-bottom:0;
            }
            .greeting2{
                font-weight:700;
            }

        }
    }
}
.content-second{
    height:100vh;
    background-color: beige;
}

//media query include
@include mobile{//458px~757px까지
    .content-first{
        position:static;
        .content-header{
            padding-bottom:10px;
            position:sticky;
            top:0;
            transition:all .5s;
            .img-div{
                width:70px;
                height:70px;
                .logo-img{
                    width:70px;
                    height:70px;
                }
            }
            span{
                font-size:18px;
                color:#000;
                &:first-child::first-letter{
                    color:#000;
                }
            }
        }
        .content-main{
            .main-img{
                position:absolute;
                clip: rect(0px, 508px, 370px, 0px);
            }
            .main-video{
                width:80%;
                height:60vh;
                margin:0 auto;

            }
            .greeting-main{
                top:20%;
                width:68%;
                margin:0 auto;
                text-align:left;
                letter-spacing: -1px;
                .greeting2{
                    font-size:25px;
                    letter-spacing: -2px;
                }
            }
        }
    }
}
